import Divider from "./divider";
import Profil from "../profileComponent/profil";
import Home from "./home";
import { useState } from "react";
import ConversationScreen from "../messages/conversationScreen";

function Body() {
  const [homee, setHome] = useState(true);
  const [profil, setProfil] = useState(false);
  const [message, setMessage] = useState(false);
  function sethome(homee) {
    setProfil(false);
    setHome(true);
    setMessage(false);

  }
  function setprofil(profil) {
    setHome(false);
    setProfil(true);
    setMessage(false);
  }
  function setmessage(message) {
    setHome(false);
    setProfil(false);
    setMessage(true);

  }

  return (
    <div className="row">
      <Divider sethome={sethome} setprofil={setprofil} setmessage={setmessage} />
      
        if(homee && !profil && !message){
          <Home />
        }
        else if(!homee && profil && !message){
          <Profil />
        }else{
          <ConversationScreen />
        }
      
    </div>
  );
}
export default Body;
