import "./conversationScreen.css";
import "./usersList.css";
import { useState } from "react";
function UsersList() {
  const [clients, setClients] = useState(false);
  const [coachs, setCoachs] = useState(false);
  const [sup, setSup] = useState(false);
  function chooseType(type) {
        if (type ==="clients"){
              
        setClients(true);
        setCoachs(false);
        setSup(false);
        console.log(clients);
        }
        else if(type==="coachs"){
              
        setClients(false);
        setCoachs(true);
        setSup(false);
        console.log(coachs);
        }
        else{
            setClients(false);
            setCoachs(false);
            setSup(true);
            console.log(sup);
        }
   
  }
  return (
    <div className="usersListWrapper">
      <div className="usersType">
        <div
          className="type"
          onClick={chooseType("clients")}
        >
          Clients
        </div>
        <div  className="type" >
          Coachs
        </div>
        <div  className="type" >
          Superviseurs
        </div>
      </div>
    </div>
  );
}
export default UsersList;
