import { useState, useEffect,useContext } from "react";
import Article from "./Article";
import  AuthContext  from '../context/authContext';
import currentUser from '../hooks/currentEmployee'
function Articles(props) {
  const [isLoading, setIsLoading] = useState(true);
  const auth = useContext(AuthContext);
  const [loadedArticles, setLoadingArticles] = useState([]);
  const user = currentUser() ;
  useEffect(() => {
    setIsLoading(true);
    try {
      fetch("http://localhost:5000/posts")
        .then((response) => response.json())
        .then((data) => {
          console.log("data", data.posts);
          setLoadingArticles(data.posts.reverse());
          setIsLoading(false);
          console.log(loadedArticles);
          console.log("currentUser houwa : " + user);
        });
    } catch (err) {} ;
   
  
}, []);


  if (isLoading) {
    return (
      <div>
        <p>Loading..</p>
      </div>
    );
  }
  return loadedArticles.length === 0 ? (
    <div>No articles yet</div>
  ) : (
    <div>
      {loadedArticles.map((article) => (
        <Article
          key={article._id}
          creator={auth.employee}
          title={article.title}
          content={article.content}
          // image={article.image}
        />
      ))}
    </div>
  );
}
export default Articles;
