const initialState = {
  count: 0,
  minus: 1,
};
const redConst = (state = initialState, action) => {
  switch (action.type) {
    case "INC":
      return {
        ...state,
        count: state.count + action.payload,
      };
    case "DEC":
      return {
        ...state,
        count: state.count - action.payload,
      };
    default:
      return state;
  }
};
export default redConst;
