import React from "react";
import useAuth from "./hooks/auth-hook";
import { AuthContext } from "./context/authContext";
import ConnectAdmin from "./coach/connectAdmin";
import ConnectSup from "./coach/connectSup";
import Body from "./components/Body";
import NavigationBar from "./components/navigationBar";
import LogIn from "./coach/LogInEmployee";
import All from "./components/all";
import { Route, BrowserRouter as Router, Switch } from "react-router-dom";
function App() {
  
  const { token, login, logout, EmployeeId } = useAuth();
  let routes;

  if (token) {
    
    switch(EmployeeId.typeEmployee ){
      case  "Administrateur":
        routes = (<ConnectAdmin /> ) 
        return(routes )  ;
      case  "Superviseur":
        routes = (
          <Router>
      <Switch>
        <div className="row">
        <ConnectSup /> 
        <div>
            <NavigationBar />
             <Body />
           </div>-
          <Route path="/" exact component={ConnectSup} />
          <Route path="/a" exact component={All} />
        </div>
      </Switch>
    </Router>
        )
        return(routes);
        case  "Coach":
          routes = (
             <All />
            )
          return(routes) ;
        default : return (routes = (<div>error </div>))
    };
  } else {
    routes = <LogIn />;
  }
  return (
    <div>
      <AuthContext.Provider
        value={{
          isLoggedIn: !!token,
          token: token,
          EmployeeId: EmployeeId,
          login: login,
          logout: logout,
        }}
      >
        <main>{routes}</main>
      </AuthContext.Provider>
    </div>
  );
}
export default App;
