import React, { useEffect, useState, useContext } from "react";
import useAuth from "./hooks/auth-hook";
import { AuthContext } from "./context/authContext";

import Body from "./components/Body";
import NavigationBar from "./components/navigationBar";
import LogIn from "./coach/LogInEmployee";
function App() {
  const auth = useContext(AuthContext);
  const [employ, setEmployee] = useState(null);

  const { token, login, logout, EmployeeId } = useAuth();
  let routes;

  if (token) {
    try {
      const a = auth.EmployeeId;
      console.log(a);
      fetch(`http://localhost:5000/employee/employee/:${a}`)
        .then((response) => response.json())
        .then((data) => {
          console.log("emplyee data mte3i" + data);
          setEmployee(data.employee);
          auth.employee = employ ;
        });
    } catch (err) {}
    routes = (
      <div>
        <NavigationBar />
        <Body />
      </div>
    );
  } else {
    routes = <LogIn />;
  }
  return (
    <div>
      <AuthContext.Provider
        value={{
          isLoggedIn: !!token,
          token: token,
          EmployeeId: EmployeeId,
          employee : employ,
          login: login,
          logout: logout,
        }}
      >
        <main>{routes}</main>
      </AuthContext.Provider>
    </div>
  );
}
export default App;
