import "./conversationScreen.css";
import "./usersList.css";
import { useState } from "react";
function UsersList() {
  const [clients, setClients] = useState(false);
  const [coachs, setCoachs] = useState(false);
  const [sup, setSup] = useState(false);
  function chooseType(type) {
    switch (type) {
      case "clients":
        setClients(true);
        setCoachs(false);
        setSup(false);
        console.log(clients);

        break;
      case "coachs":
        setClients(false);
        setCoachs(true);
        setSup(false);
        console.log(coachs);
        break;
      case "sup":
        setClients(false);
        setCoachs(false);
        setSup(true);
        console.log(sup);

        break;

      default:
        break;
    }
  }
  return (
    <div className="usersListWrapper">
      <div className="usersType">
        <div
          className={`${clients ? "type" : "clicked"}`}
          onClick={chooseType("clients")}
        >
          Clients
        </div>
        <div  className={`${coachs ? "type" : "clicked"}`} onClick={chooseType("coachs")}>
          Coachs
        </div>
        <div  className={`${sup ? "type" : "clicked"}`} onClick={chooseType("sup")}>
          Superviseurs
        </div>
      </div>
    </div>
  );
}
export default UsersList;
