import { useState, useEffect } from "react";
function Articles(props) {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedArticles, setLoadingArticles] = useState([]);

  useEffect(() => {
    const fetchPosts = async() => {
      /*try {
        setIsLoading(true);
        fetch("http://localhost:5000/posts")
          .then((response) => {
            return response.json();
          })
          .then((res) => {
            console.log("ow el bodii " + res);
            console.log("het el data saaa7byy " + res.posts);
          });
      } catch (err) {
        
      }*/
      setIsLoading(true);
      var response = await fetch("http://localhost:5000/posts");
      var body = await response.json();
      console.log(body.posts);
      
    };
    fetchPosts();
    setIsLoading(false);
    console.log(loadedArticles);
    const posts = [];
    for(const key in loadedArticles){
      const post ={
        id : key,
        ...loadedArticles[key]
      };
      posts.push(post);
    }
    
    setLoadingArticles(posts) ;
    console.log(loadedArticles);
    
  }, []);

  if (isLoading) {
    return (
      <div>
        <p>Loading..</p>
      </div>
    );
  }
  return loadedArticles.length === 0 ? (
    <div>No articles yet</div>
  ) : (
    <div>
      
      {loadedArticles.map((article) => (
        <article
          key={article.id}
          //creator={article.creator}
          title={article.title}
          content={article.content}
          // image={article.image}
        />
      ))}
      
    </div>
  );
}
export default Articles;
