import "./conversationScreen.css";
import "./usersList.css";
import { useState, useEffect } from "react";
function UsersList() {
  const [clients, setClients] = useState(false);
  const [coachs, setCoachs] = useState(false);
  const [sup, setSup] = useState(false);
  function ChooseType(type) {
        useEffect(() => {
            if (type ==="clients"){
              
                  setClients(true);
                  setCoachs(false);
                  setSup(false);
                  console.log(clients);
                  }
                  else if(type==="coachs"){
                        
                  setClients(false);
                  setCoachs(true);
                  setSup(false);
                  console.log(coachs);
                  }
                  else{
                      setClients(false);
                      setCoachs(false);
                      setSup(true);
                      console.log(sup);
                  }
        }, [type])
   
  }
  return (
    <div className="usersListWrapper">
      <div className="usersType">
        <div
          className={clients? "clicked" : "type" }
          onClick={ChooseType("clients")}
        >
          Clients
        </div>
        <div  className={coachs? "clicked" : "type" } onClick={ChooseType("clients")} >
          Coachs
        </div>
        <div  className={sup? "clicked" : "type" } onClick={ChooseType("clients")} >
          Superviseurs
        </div>
      </div>
    </div>
  );
}
export default UsersList;
