import { useState, useEffect,useContext } from "react";
import Article from "./Article";
import  AuthContext  from '../context/authContext';
function Articles(props) {
  const [isLoading, setIsLoading] = useState(true);
  const [employ, setEmployee] = useState(true);
  const [loadedArticles, setLoadingArticles] = useState([]);
  const auth = useContext(AuthContext);
  useEffect(() => {
    setIsLoading(true);
    try {
      fetch("http://localhost:5000/posts")
        .then((response) => response.json())
        .then((data) => {
          console.log("data", data.posts);
          setLoadingArticles(data.posts.reverse());
          setIsLoading(false);
          console.log(loadedArticles);
          console.log(auth.EmployeeId.nom);
        });
    } catch (err) {} ;
   /* try {
      const a = auth.EmployeeId ;
      //console.log(auth.EmployeeId);
      console.log(a);
      fetch(`http://localhost:5000/employee/employee/:$a`)
        .then((response) => response.json())
        .then((data) => {
          console.log("data"+ data);
          setEmployee(data.employee);
        });
    } catch (err) {}*/
  
}, []);


  if (isLoading) {
    return (
      <div>
        <p>Loading..</p>
      </div>
    );
  }
  return loadedArticles.length === 0 ? (
    <div>No articles yet</div>
  ) : (
    <div>
      {loadedArticles.map((article) => (
        <Article
          key={article.id}
          creator={auth.EmployeeId.nom}
          title={article.title}
          content={article.content}
          // image={article.image}
        />
      ))}
    </div>
  );
}
export default Articles;
